gci -rec conanfile.py | % { $cmakeTarget = $null; $cmakeFile = $null;  $c = get-content $_ -raw; if ($c -match "self\.cpp_info\.set_property\(`"cmake_target_name`", `"([^`"]*)`"\)") { $cmakeTarget = $Matches[1] } if ($c -match "self\.cpp_info\.set_property\(`"cmake_file_name`", `"([^`"]*)`"\)") { $cmakeFile = $Matches[1] } if ($cmakeTarget -or $cmakeFile) { $package = $_.Directory.Parent.Name ; "$package,$cmakeFile,$cmakeTarget" } }